SELECT customer_id, firstname, surname INTO OUTFILE
\'/exportdata/customers.txt\'
 FIELDS TERMINATED BY \',\' OPTIONALLY ENCLOSED BY \'"\'
 LINES TERMINATED BY \'\\n\'
 FROM customers;
 


